  const consume = () => tokens[pos++]

  function parseWhiteSpace () {
    while(/\s/.test(peek())){
      consume()
    }
  }

  const parseNumber = () => {
    let digitStr = ''
    while(/\d/.test(peek())){
      digitStr += consume()
    }
    return parseFloat(digitStr)
  }

  const parseAccidental = () => {
    const result = {
      isAccidental : false,
      accidental : ""
    }
    if(/[#bs]/i.test(peek())){
      result.isAccidental = true 
      result.accidental = consume()
    }
    return result
  }

  const parseOctave = () => {
    const result = {
      octave : 4
    }
    const octave = parseNumber()
    if(!isNaN(octave)){
