      name,
      ...accidental,
      ...parseOctave(),
    }
  }

  function parseChord(){
    const parseType = () => {
      let typeStr = ''
      while(/[\w]/i.test(peek())){
        typeStr += consume()
      }
      return {
        type : typeStr ? retrieveChordType(typeStr) : CHORD_TYPES.MAJOR
      }
    }

