    const notes = []
    if(/{/.test(peek())){
      while(!/}/.test(peek())){
        // consume any non alpha-numerics (separators, whitespace, etc.)
        while(/[\W_]/.test(peek())) consume()
        notes.push(parseNote())
      }
      parseWhiteSpace()
      consume() // consume final }
    }
    return notes.length ? notes : null
  }
