  const chords = []
  while(peek()){
    parseWhiteSpace()
    let chord = null
    if(chord = parseNoteGroup()){
      chords.push(chord)
    }else if(chord = parseChord()){
      chords.push(chord)
    }
  }
  console.log(chords)
}

